(650, 'wpmletsubject_posts', '', 'yes'),
(651, 'wpmletmessage_posts', '<div class="wpmlposts">\n		[newsletters_post_loop]\n			<div class="wpmlpost">\n				<h3>[newsletters_post_anchor][newsletters_post_title][/newsletters_post_anchor]</h3>\n				[newsletters_post_date_wrapper]<p><small>Posted on [newsletters_post_date format="F jS, Y"] by [newsletters_post_author]</small></p>[/newsletters_post_date_wrapper]\n				<div class="wpmlpost_content">\n					[newsletters_post_thumbnail]\n					[newsletters_post_excerpt]\n				</div>\n			</div>\n			<hr style="visibility:hidden; clear:both;" />\n		[/newsletters_post_loop]\n	</div>', 'yes'),
(652, 'wpmletsubject_latestposts', '', 'yes'),
(653, 'wpmletmessage_latestposts', '<div class="wpmlposts">\n		[newsletters_post_loop]\n			<h2>[newsletters_category_heading]</h2>\n			<div class="wpmlpost">\n				<h3>[newsletters_post_anchor][newsletters_post_title][/newsletters_post_anchor]</h3>\n				[newsletters_post_date_wrapper]<p><small>Posted on [newsletters_post_date format="F jS, Y"] by [newsletters_post_author]</small></p>[/newsletters_post_date_wrapper]\n				<div class="wpmlpost_content">\n					[newsletters_post_thumbnail]\n					[newsletters_post_excerpt]\n				</div>\n			</div>\n			<hr style="visibility:hidden; clear:both;" />\n		[/newsletters_post_loop]\n	</div>', 'yes'),
(654, 'wpmletsubject_sendas', '', 'yes'),
(655, 'wpmletmessage_sendas', '<div class="newsletter_posts">\n		[newsletters_post_loop]\n			<div class="newsletter_post newsletter_sendas">\n				[newsletters_post_date_wrapper]<p><small>Posted on [newsletters_post_date format="F jS, Y"] by [newsletters_post_author]</small></p>[/newsletters_post_date_wrapper]\n				<div class="newsletter_post_content">\n					[newsletters_post_excerpt]\n				</div>\n			</div>\n		[/newsletters_post_loop]\n	</div>', 'yes'),
(656, 'wpmletsubject_confirm', 'Confirm Subscription', 'yes'),
(657, 'wpmletmessage_confirm', 'Good day,\r\n\r\nThank you for subscribing to the mailing list(s): [wpmlmailinglist].\r\nPlease click the link below to activate/confirm your subscription.\r\n\r\n[wpmlactivate]\r\n\r\nAll the best,\r\n[wpmlblogname]', 'yes'),
(658, 'wpmletsubject_bounce', 'Email Bounced', 'yes'),
(659, 'wpmletmessage_bounce', 'Good day,\r\n\r\nAn email has bounced.\r\nThe email address is: [wpmlemail].\r\nTotal bounces for this email/subscriber: [wpmlbouncecount].\r\n\r\nAll the best,\r\n[wpmlblogname]', 'yes'),
(660, 'wpmletsubject_unsubscribe', 'Unsubscription', 'yes'),
(661, 'wpmletmessage_unsubscribe', 'Good day Administrator,\r\n\r\nA subscriber has unsubscribed from a mailing list.\r\nThe mailing list is: [wpmlmailinglist].\r\nThe subscriber email is: [wpmlemail].\r\n\r\n[wpmlunsubscribecomments]\r\n\r\nAll the best,\r\n[wpmlblogname]', 'yes'),
(662, 'wpmletsubject_unsubscribeuser', 'You are unsubscribed', 'yes'),
(663, 'wpmletmessage_unsubscribeuser', 'Your e-mail has been removed from our database.\r\nYou will no longer receive communication from us.\r\nThanks for your readership, and we hope you''ll visit us again!\r\n\r\nWas this a mistake? If it was, you can [newsletters_resubscribe]', 'yes'),
(664, 'wpmletsubject_unsubscribeconfirm', 'Confirm Unsubscribe', 'yes'),
(665, 'wpmletmessage_unsubscribeconfirm', 'Good day,\r\n\r\nPlease confirm your unsubscribe:\r\n\r\n[newsletters_unsubscribelink]\r\n\r\nAll the best,\r\n[wpmlblogname]', 'yes'),
(666, 'wpmletsubject_expire', 'Subscription Expired', 'yes'),
(667, 'wpmletmessage_expire', 'Good Day,\r\n\r\nYour subscription has expired.\r\nThe mailing list is: [wpmlmailinglist].\r\nPlease click the link below to renew your subscription.\r\n\r\n[wpmlactivate]\r\n\r\nAll the best,\r\n[wpmlblogname]', 'yes'),
(668, 'wpmletsubject_order', 'Paid Subscription', 'yes'),
(669, 'wpmletmessage_order', 'Good day Administrator,\r\n\r\nYou have received a paid mailing list subscription order.\r\nThe mailing list is: [wpmlmailinglist].\r\nThe subscriber email is: [wpmlemail].\r\n\r\nAll the best,\r\n[wpmlblogname]', 'yes'),
(670, 'wpmletsubject_schedule', 'Email Cron Fired', 'yes'),
(671, 'wpmletmessage_schedule', 'Good day Administrator,\r\n\r\nYour email cron has been fired as scheduled.\r\n\r\nAll the best,\r\n[wpmlblogname]', 'yes'),
(672, 'wpmletsubject_subscribe', 'New Subscription', 'yes'),
(673, 'wpmletmessage_subscribe', 'Good day Administrator,\r\n\r\nA user/visitor has just subscribed to: [wpmlmailinglist].\r\nThe email address of this subscriber is: [wpmlemail].\r\n\r\n[wpmlcustomfields]\r\n\r\nAll the best,\r\n[wpmlblogname]', 'yes'),
(674, 'wpmletsubject_authenticate', 'Authenticate Email Address', 'yes'),
(675, 'wpmletmessage_authenticate', 'Please authenticate your subscriber account by clicking the link below.\\r\\n\\r\\n[newsletters_authenticate]\\r\\n\\r\\nOnce you authenticate, you can manage your subscriptions and additional subscriber information.\\r\\n\\r\\nAll the best,\\r\\n[wpmlblogname]', 'yes'),
