(18, 1, '2016-09-13 07:25:29', '2016-09-13 07:25:29', 'Welcome to WordPress. This is your first post. Edit or delete it, then start writing!', 'Hello world!', '', 'inherit', 'closed', 'closed', '', '1-revision-v1', '', '', '2016-09-13 07:25:29', '2016-09-13 07:25:29', '', 1, 'http://dev.pragmaticlinux:8080/?p=18', 0, 'revision', '', 0),
(19, 1, '2016-09-13 07:25:52', '2016-09-13 07:25:52', 'Pragmatic Linux is build to assist Software Developers, Web Developers and Server Administrators in being more productive during their work by offering tools for managing projects more easily, with an easier user interface. Pragmatic Linux is free and is available for business use (within in Intranet).', 'What is it for?', '', 'publish', 'open', 'open', '', 'what-is-it-for', '', '', '2016-09-14 21:00:12', '2016-09-14 21:00:12', '', 0, 'http://dev.pragmaticlinux:8080/?p=19', 0, 'post', '', 0),
