(126, 1, '2015-01-31 11:00:35', '2015-01-31 11:00:35', '<span id="result_box" class=""><span class="hps">We</span> <span class="hps">are</span> <span class="hps">obligated to</span> <span class="hps">pause,</span> <span class="hps">pragmatic</span> linux (Developer) <span class="hps">developments </span><span class="hps">for</span> <span class="hps">extraneous reasons</span> of <span class="hps">financial</span> problems <span class="hps">and</span> <span class="hps">personal</span> <span class="hps">obligations</span>, the project <span class="hps">will be delayed</span> <span class="hps">until March</span> <span class="hps">or</span> <span class="hps">April. We will notify for every news about project.</span></span>\r\n\r\n<span id="result_box" class=""><span class="hps">We apologize</span> <span class="hps">for the delay</span> <span class="hps">and</span> <span class="hps">postponement of</span> <span class="hps">development</span>.</span>', 'Delay development Pragmatic Linux - Developer (March-April)', '', 'publish', 'open', 'open', '', 'delay-development-pragmatic-linux-developer-march-april', '', '', '2016-09-16 20:34:00', '2016-09-16 20:34:00', '', 0, 'http://dev.pragmaticlinux:8080/?p=126', 0, 'post', '', 0),
(127, 1, '2016-09-16 20:34:00', '2016-09-16 20:34:00', '<span id="result_box" class=""><span class="hps">We</span> <span class="hps">are</span> <span class="hps">obligated to</span> <span class="hps">pause,</span> <span class="hps">pragmatic</span> linux (Developer) <span class="hps">developments </span><span class="hps">for</span> <span class="hps">extraneous reasons</span> of <span class="hps">financial</span> problems <span class="hps">and</span> <span class="hps">personal</span> <span class="hps">obligations</span>, the project <span class="hps">will be delayed</span> <span class="hps">until March</span> <span class="hps">or</span> <span class="hps">April. We will notify for every news about project.</span></span>\r\n\r\n<span id="result_box" class=""><span class="hps">We apologize</span> <span class="hps">for the delay</span> <span class="hps">and</span> <span class="hps">postponement of</span> <span class="hps">development</span>.</span>', 'Delay development Pragmatic Linux - Developer (March-April)', '', 'inherit', 'closed', 'closed', '', '126-revision-v1', '', '', '2016-09-16 20:34:00', '2016-09-16 20:34:00', '', 126, 'http://dev.pragmaticlinux:8080/126-revision-v1/', 0, 'revision', '', 0);
