(6, 'Simply Elegant', 'simplyelegant', 'Y', 'paste', '<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd">\r\n<html lang="en">\r\n<head>\r\n   <meta http-equiv="Content-Type" content="text/html; charset=utf-8">\r\n   <title>[wpmlsubject]</title>\r\n   <style type="text/css" media="screen">\r\n      body {\r\n         background-color: #f5f6f4;\r\n         margin: 0;\r\n         padding: 0;\r\n      }\r\n\r\n      a img {\r\n         border: none;\r\n      }\r\n\r\n      a {\r\n         border: none;\r\n      }\r\n\r\n      td.permission {\r\n         padding: 30px 0 10px 0;\r\n      }\r\n\r\n      .permission {\r\n         font-family: ''Lucida Grande'';\r\n         font-size: 10px;\r\n         color: #666666;\r\n         padding: 4px 0 4px 0;\r\n      }\r\n\r\n      .permission a {\r\n         color: #333333;\r\n      }\r\n\r\n      .permission p {\r\n         margin: 0 0 4px 0;\r\n      }\r\n\r\n      .header {\r\n         background-color: #353535;\r\n      }\r\n\r\n      .header h1 {\r\n      	font-family: Georgia;\r\n      	font-size: 32px;\r\n      	font-weight: normal;\r\n      	color: #bfbfbf;\r\n      	display: inline;\r\n      	text-align: left;}\r\n\r\n      .date h3 {\r\n      	font-family: Georgia;\r\n      	font-size: 14px;\r\n      	color: #bfbfbf;\r\n      	font-weight: normal;\r\n      	text-align: right;\r\n      	display: inline;\r\n      	font-style: italic;\r\n      }\r\n\r\n      .body {\r\n         background-color: #ffffff;\r\n      }\r\n\r\n      td.sidebar {\r\n      	padding: 22px 0 0 0;\r\n      }\r\n\r\n      .sidebar h2 {\r\n         font-family: Georgia;\r\n         font-size: 18px;\r\n         font-weight: bold;\r\n         color: #808080;\r\n         margin: 0 0 6px 4px;\r\n         padding: 0;\r\n         text-align: left;\r\n      }\r\n\r\n      .sidebar h2.issue {\r\n         color: #000000;\r\n         margin: 0 0 10px 10px;\r\n      }\r\n\r\n      .sidebar ul {\r\n         font-family: Georgia;\r\n         font-size: 12px;\r\n         color: #2679b9;\r\n         margin: 0 0 0 24px;\r\n         padding: 0;\r\n         text-align: left;\r\n      }\r\n\r\n      .sidebar ul a {\r\n      	font-family: Georgia;\r\n      	font-size: 12px;\r\n      	color: #2679b9;\r\n      	font-style: italic;\r\n      }\r\n      .sidebar ul li {\r\n      	margin: 0 0 10px 0;\r\n      }\r\n      .sidebar p {\r\n         font-family: ''Lucida Grande'';\r\n         font-size: 11px;\r\n         color: #5f5f5f;\r\n         padding: 0;\r\n         margin: 0 0 10px 0;\r\n         text-align: left;\r\n      }\r\n\r\n      td.sidebg {\r\n         background-color: #f1f1f1;\r\n         padding: 10px 16px 10px 16px;\r\n      }\r\n\r\n      td.mainbar {\r\n      	padding: 22px 0 0 14px;\r\n      	text-align: left;\r\n      }\r\n\r\n      .mainbar p {\r\n      	font-family: ''Lucida Grande'';\r\n      	font-size: 12px;\r\n      	color: #333333;\r\n      	margin: 0 0 10px 0;\r\n      	text-align: left;\r\n      }\r\n\r\n      .mainbar p.first {\r\n      	margin-top: 10px;\r\n      }\r\n\r\n      .mainbar h2 {\r\n      	font-family: ''Lucida Grande'';\r\n      	font-size: 16px;\r\n      	color: #000000;\r\n      	text-transform: uppercase;\r\n      	margin: 10px 0 16px 0;\r\n      }\r\n\r\n      .mainbar h2 a {\r\n      	font-family: ''Lucida Grande'';\r\n      	font-size: 16px;\r\n      	color: #000000;\r\n      	text-transform: uppercase;\r\n      	text-decoration: none;\r\n      	font-style: normal;\r\n      }\r\n\r\n      .mainbar a {\r\n      	font-family: Georgia;\r\n      	font-size: 13px;\r\n      	color: #2679b9;\r\n      	font-style: italic;\r\n      }\r\n\r\n      .mainbar a.center {\r\n      	font-size: 12px;\r\n      	text-align: center;\r\n      	display: block;\r\n      	color: #999999;\r\n      	padding: 8px 0 12px 0;\r\n      	text-decoration: none;\r\n      }\r\n\r\n      .mainbar img.inline {\r\n      	border: 1px solid #dedede;\r\n      	padding: 4px;\r\n      }\r\n\r\n      td.footer {\r\n      	padding: 0 0 10px 0;\r\n      	border-top: 2px solid #ededed;\r\n      }\r\n\r\n      .footer p {\r\n      	color: #a1a1a1;\r\n      	font-size: 11px;\r\n      	margin: 0;\r\n      	padding: 0;\r\n      }\r\n\r\n      .footer p.first {\r\n      	margin: 14px 0 0 0;\r\n      }\r\n\r\n      .footer a {\r\n      	font-family: ''Lucida Grande'';\r\n      	font-size: 11px;\r\n      	color: #2679b9;\r\n      }\r\n   </style>\r\n\r\n</head>\r\n<body>\r\n\r\n[wpmltrack]\r\n\r\n<table width="100%" cellspacing="0" cellpadding="0" bgcolor="#f5f6f4">\r\n   <tr>\r\n      <td align="center">\r\n         <table width="600" cellspacing="0" cellpadding="0">\r\n            <tr>\r\n               <td align="center" class="permission">\r\n                  <p>You''re receiving this newsletter because you subscribed to our mailing list.</p>\r\n                  <p>Not interested anymore? [wpmlunsubscribe]. <br/>Having trouble viewing this email? [wpmlonline].</p>\r\n               </td>\r\n            </tr>\r\n            <tr>\r\n               <td height="77" align="left" class="header">\r\n                  <table width="600" border="0" cellspacing="0" cellpadding="0">\r\n                     <tr>\r\n                        <td><img src="http://192.168.0.104:8080/wp-content/plugins/newsletters-lite/includes/themes/simplyelegant/images/header.gif" width="600" height="77" alt="ABC Widgets"></td>\r\n                     </tr>\r\n                  </table>\r\n               </td>\r\n            </tr>\r\n         </table>\r\n      </td>\r\n   </tr>\r\n   <tr>\r\n      <td align="center">\r\n         <table width="600" cellspacing="10" cellpadding="0" class="body">\r\n            <tr>               \r\n               <td align="center" valign="top" class="mainbar">\r\n                  <h2>[wpmlsubject]</h2>\r\n\r\n                  [wpmlcontent]\r\n                  \r\n                  <a href="#top" class="center">Back to top^</a>\r\n                  \r\n                  <img src="http://192.168.0.104:8080/wp-content/plugins/newsletters-lite/includes/themes/simplyelegant/images/hr-large.gif" width="560" height="27" alt="Hr">\r\n               </td>\r\n            </tr>\r\n            <tr>\r\n               <td align="center">\r\n                  <table width="560" border="0" cellspacing="0" cellpadding="0">\r\n                     <tr>\r\n                        <td class="footer" align="left">\r\n                           <p class="first">Copyright &copy; <strong>[wpmlblogname]</strong> [wpmldate format="%Y"]</p>\r\n                        </td>\r\n                     </tr>\r\n                  </table>\r\n               </td>\r\n            </tr>\r\n         </table>\r\n      </td>\r\n   </tr>\r\n</table>\r\n\r\n</body>\r\n</html>', 'N', '', '', '2016-09-30 20:25:45', '2016-09-30 20:25:45');
INSERT INTO `pl_wpmlthemes` (`id`, `title`, `name`, `premade`, `type`, `content`, `def`, `defsystem`, `acolor`, `created`, `modified`) VALUES
(7, 'Snazzy', 'snazzy', 'Y', 'paste', '<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd">\r\n<html>\r\n<head>\r\n   <meta http-equiv="Content-Type" content="text/html; charset=utf-8">\r\n   <title>[wpmlsubject]</title>\r\n   <style type="text/css" media="screen">\r\n			#outlook a{padding:0;} /* Force Outlook to provide a "view in browser" button. */\r\n			body{width:100% !important;} .ReadMsgBody{width:100%;} .ExternalClass{width:100%;} /* Force Hotmail to display emails at full width */\r\n			body{-webkit-text-size-adjust:none;} /* Prevent Webkit platforms from changing default text sizes. */\r\n\r\n			body {margin:0; padding:0;}\r\n			img {border:0; outline:none; text-decoration:none;}\r\n			table td {border-collapse:collapse;}\r\n			\r\n			h1, .h1{\r\n				color:#333333 !important;\r\n				display:block;\r\n				font-family:Arial;\r\n				font-size:24px;\r\n				font-weight:bold;\r\n				line-height:100%;\r\n				margin-top:0;\r\n				margin-right:0;\r\n				margin-bottom:10px;\r\n				margin-left:0;\r\n				text-align:left;\r\n			}\r\n\r\n			h2, .h2{\r\n				color:#333333 !important;\r\n				display:block;\r\n				font-family:Arial;\r\n				font-size:18px;\r\n				font-weight:bold;\r\n				line-height:100%;\r\n				margin-top:0;\r\n				margin-right:0;\r\n				margin-bottom:10px;\r\n				margin-left:0;\r\n				text-align:left;\r\n			}\r\n\r\n			h3, .h3{\r\n				color:#333333 !important;\r\n				display:block;\r\n				font-family:Arial;\r\n				font-size:16px;\r\n				font-weight:bold;\r\n				line-height:100%;\r\n				margin-top:0;\r\n				margin-right:0;\r\n				margin-bottom:10px;\r\n				margin-left:0;\r\n				text-align:left;\r\n			}\r\n\r\n			h4, .h4{\r\n				color:#333333 !important;\r\n				display:block;\r\n				font-family:Arial;\r\n				font-size:14px;\r\n				font-weight:bold;\r\n				line-height:100%;\r\n				margin-top:0;\r\n				margin-right:0;\r\n				margin-bottom:10px;\r\n				margin-left:0;\r\n				text-align:left;\r\n			}\r\n			\r\n			h5, .h5{\r\n				color:#333333 !important;\r\n				display:block;\r\n				font-family:Arial;\r\n				font-size:14px;\r\n				font-weight:bold;\r\n				line-height:100%;\r\n				margin-top:0;\r\n				margin-right:0;\r\n				margin-bottom:10px;\r\n				margin-left:0;\r\n				text-align:left;\r\n			}\r\n			\r\n			div{\r\n				color:#666666 !important;\r\n				font-family:Arial;\r\n				font-size:14px;\r\n				line-height:150%;\r\n				text-align:left;\r\n			}\r\n   </style>\r\n\r\n</head>\r\n    <body style="background-color:#FFFFFF;" leftmargin="0" marginwidth="0" topmargin="0" marginheight="0" offset="0">\r\n    	[wpmltrack]\r\n    	<center>\r\n        	<table border="0" cellpadding="0" cellspacing="0" height="100%" width="100%" style="background:#FFFFFF;" id="backgroundTable">\r\n            	<tr>\r\n                	<td align="center" valign="top">\r\n					\r\n						<!-- BEGIN TOP SECTION -->\r\n                        <table border="0" cellpadding="0" cellspacing="0" width="580" style="background:#FFFFFF;">\r\n                            <tr>\r\n                                <td valign="top">\r\n                                    <table border="0" cellpadding="15" cellspacing="0" width="580">\r\n                                    	<tr>\r\n                                        	<td valign="top">\r\n                                            	<div style="color:#000 !important; font-size:11px; line-height:16px; font-family:Arial, sans-serif;">\r\n													<center>\r\n														<p>\r\n															You''re receiving this newsletter because you subscribed to our mailing list.<br />\r\n															Not interested anymore? [wpmlunsubscribe]. <br/>\r\n															Having trouble viewing this email? [wpmlonline].\r\n														</p>\r\n													</center>\r\n                                                </div>\r\n                                            </td>\r\n                                        </tr>\r\n                                    </table>\r\n                                </td>\r\n                            </tr>\r\n                        </table>\r\n						<!-- END TOP SECTION -->\r\n						\r\n						<!-- BEGIN CONTENT SECTION -->\r\n                    	<table border="0" cellpadding="0" cellspacing="0" width="580" style="background:#FFFFFF;">\r\n						\r\n							<!-- BEGIN HEADER SECTION -->\r\n                        	<tr>\r\n                            	<td style="background-color:#FFFFFF;" align="center" valign="bottom">\r\n									<img src="http://192.168.0.104:8080/wp-content/plugins/newsletters-lite/includes/themes/snazzy/images/header.jpg" width="580" height="119" />\r\n                                </td>\r\n                            </tr>\r\n							<!-- END HEADER SECTION -->\r\n							\r\n							<!-- BEGIN TEXT SECTION -->\r\n                        	<tr>\r\n                            	<td valign="top" align="center">\r\n                                    <table border="0" cellspacing="0" cellpadding="20" width="580">\r\n                                    	<tr>\r\n                                        	<td valign="top">\r\n                                            	<div style="border-top:8px solid #ED5D9A; border-bottom:8px solid #ED5D9A; color:#333; font-size:14px; line-height:21px; font-family:Helvetica, Arial, sans-serif;">												   \r\n												   <br />\r\n												   <h2 style="color:#ED5D9A !important;">[wpmlsubject]</h2>\r\n												   [wpmlcontent]\r\n												   <br />\r\n												   <br />\r\n												</div>\r\n                                            </td>\r\n                                        </tr>\r\n                                    </table>\r\n                                </td>\r\n                            </tr>\r\n							<!-- END TEXT SECTION -->\r\n							<!-- BEGIN FOOTER SECTION -->\r\n                        	<tr>\r\n								<td valign="middle" align="middle">\r\n									<p style="color:#999 !important; font-family:Arial, sans-serif; font-size:12px;">Copyright &copy; <strong>[wpmlblogname]</strong> [wpmldate format="%Y"]</p>\r\n                                </td>\r\n                            </tr>\r\n							<!-- END FOOTER SECTION -->\r\n						</table>\r\n						<!-- END CONTENT SECTION -->\r\n                    </td>\r\n                </tr>\r\n            </table>\r\n			<br />\r\n			<br />\r\n        </center>\r\n    </body>\r\n</html>', 'N', '', '', '2016-09-30 20:25:45', '2016-09-30 20:25:45'),
(8, 'Pro News', 'pronews', 'Y', 'paste', '<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd">\r\n<html>\r\n    <head>\r\n        <meta http-equiv="Content-Type" content="text/html; charset=UTF-8" />        \r\n        <title>[wpmlsubject]</title>\r\n		<style type="text/css">\r\n			#outlook a{padding:0;} /* Force Outlook to provide a "view in browser" button. */\r\n			body{width:100% !important;} .ReadMsgBody{width:100%;} .ExternalClass{width:100%;} /* Force Hotmail to display emails at full width */\r\n			body{-webkit-text-size-adjust:none;} /* Prevent Webkit platforms from changing default text sizes. */\r\n\r\n			body{margin:0; padding:0;}\r\n			img{border:0; height:auto; line-height:100%; outline:none; text-decoration:none;}\r\n			table td{border-collapse:collapse;}\r\n			#backgroundTable{height:100% !important; margin:0; padding:0; width:100% !important;}\r\n\r\n			body, #backgroundTable{\r\n				background-color:#636363;\r\n			}\r\n\r\n			#templateContainer{\r\n				border: 6px solid #454545;\r\n			}\r\n\r\n			h1, .h1{\r\n				color:#4d4d4d;\r\n				display:block;\r\n				font-family:Arial;\r\n				font-size:34px;\r\n				font-weight:bold;\r\n				line-height:100%;\r\n				margin-top:0;\r\n				margin-right:0;\r\n				margin-bottom:10px;\r\n				margin-left:0;\r\n				text-align:left;\r\n			}\r\n\r\n			h2, .h2{\r\n				color:#4d4d4d;\r\n				display:block;\r\n				font-family:Arial;\r\n				font-size:30px;\r\n				font-weight:bold;\r\n				line-height:100%;\r\n				margin-top:0;\r\n				margin-right:0;\r\n				margin-bottom:10px;\r\n				margin-left:0;\r\n				text-align:left;\r\n			}\r\n\r\n			h3, .h3{\r\n				color:#4d4d4d;\r\n				display:block;\r\n				font-family:Arial;\r\n				font-size:26px;\r\n				font-weight:bold;\r\n				line-height:100%;\r\n				margin-top:0;\r\n				margin-right:0;\r\n				margin-bottom:10px;\r\n				margin-left:0;\r\n				text-align:left;\r\n			}\r\n\r\n			h4, .h4{\r\n				color:#4d4d4d;\r\n				di